import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import { ProductsBottomsDesc } from "../../Bottoms/ProductsBottomsDesc";
import { ProductsBottomsAsc } from "../../Bottoms/ProductsBottomsAsc";

export const DropDownPriceBottoms = () => {
    const [isOpen, setOpen] = useState(false);

    const handleDropDown = () => {
      setOpen(!isOpen);
    };

    return (
      <div className="dropdown">
        <button
          className="text-white bg-slate-700 hover:bg-slate-800 focus:ring-4 focus:outline-none focus:ring-slate-300 font-medium rounded-lg text-sm px-4 py-2.5 text-center inline-flex items-center"
          onClick={handleDropDown}
        >
          Sort By: (Price)
          <svg
            className="ml-2 w-4 h-4"
            aria-hidden="true"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M19 9l-7 7-7-7"
            ></path>
          </svg>
        </button>

        <div
          id="dropdown"
          className={`z-10 w-44 bg-white rounded divide-y divide-gray-100 shadow ${
            isOpen ? "block" : "hidden"
          }`}
        >
          <ul className=" z-10 w-44 bg-white rounded divide-y divide-gray-100 shadow ">
              <li
              >
                <Link className="block py-2 px-4 hover:bg-gray-100" to="ProductsBottomsAsc" element={<ProductsBottomsAsc />}>
                  Low to High 
                </Link>
              </li>
              <li
              >
                <Link className="block py-2 px-4 hover:bg-gray-100" to="ProductsBottomsDesc" element={<ProductsBottomsDesc />}>
                  High to Low
                </Link>
              </li>
          </ul>
        </div>
      </div>
    );
  };
