import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios"; 
import { ProductsTopAsc } from './ProductsTopAsc';
import { ProductsTopDesc } from './ProductsTopsDesc';


export const Products = () => {
    const [products,setProducts]= useState([])

    useEffect(()=>{
        const fetchAllProducts = async()=>{
            try {
                const res= await axios.get("http://localhost:8800/productslist")
                // console.log(res)
                setProducts(res.data)
            } catch(err){
                console.log(err)
            }
        }
        fetchAllProducts()
    }, [])
    const handleChange =(e) =>{
      e.preventDefault();
      setProducts(e.target.value)
    };
    if (product.length > 0){
      fetchAllProducts = fetchAllProducts.filter((i)=>{
        return i.product.match(product);
      })
    }
  return (
    <div className="w-full h-screen bg-no-repeat bg-cover">
         {/* style="background-image: url('images/wrinkledpaperbackground.webp');" */}
          <img
            src="images/shopourcollection.webp"
            alt="woman in red coat combat boots collection hero img"
          />
        <div className=" mt-8 mb-14 h-6 ">
          {" "}
          <h1 className="text-3xl p-2 border-black border border-solid font-mono uppercase text-center tracking-wider bg-yellow-200 font-bold w-3/4 m-auto">
            Product List{" "}
          </h1>
        </div>
        <button className='text-2xl font-mono  text-black font-bold hover:text-red-400'>
                <h2 className="text-2xl font-mono  text-black font-bold hover:text-red-400">
                <Link to="ProductsTopAsc" element={<ProductsTopAsc />}>
                    {" "}
                    TOPS ASC{" "}
                  </Link>
                </h2> </button>
                <button className='text-2xl font-mono  text-black font-bold hover:text-red-400'>

                <h2 className="text-2xl font-mono  text-black font-bold hover:text-red-400">
                <Link to="ProductsTopAsc" element={<ProductsTopDesc/>}>
                    {" "}
                    TOPS ASC{" "}
                  </Link>
                </h2> </button>

                <input type="text" placeholder='search item' onChange={handleChange} value={product}/> 
        <section className="w-fit m-auto grid grid-cols-1 lg:grid-cols-3 md:grid-cols-2 justify-items-center justify-center gap-y-20 gap-x-14 mt-10 mb-5"> 
            {products.map(product=>(

                <div className="w-72 h-auto bg-slate-300 shadow-md rounded-xl duration-500 hover:scale-105 hover:shadow-xl">
                  <a href="#">
                    <div className="h-100 w-72 object-cover rounded-t-xl"> {product.image && <img src={product.image} alt="Apparel Products"/>} </div>
                    <div className="px-4 py-3 w-72 bg-slate-300">
                      <span className="text-gray-900 mr-3 uppercase text-xs">{product.category}</span>
                      <p className="text-lg font-bold text-black truncate block capitalize">{product.name}</p>
                      <div className="flex items-center">
                        <p className="text-lg font-semibold text-black cursor-auto my-3">${product.price}</p>
                        <div className="ml-auto"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-bag-plus" viewBox="0 0 16 16">
                            <path fill-rule="evenodd" d="M8 7.5a.5.5 0 0 1 .5.5v1.5H10a.5.5 0 0 1 0 1H8.5V12a.5.5 0 0 1-1 0v-1.5H6a.5.5 0 0 1 0-1h1.5V8a.5.5 0 0 1 .5-.5z" />
                            <path d="M8 1a2.5 2.5 0 0 1 2.5 2.5V4h-5v-.5A2.5 2.5 0 0 1 8 1zm3.5 3v-.5a3.5 3.5 0 1 0-7 0V4H1v10a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V4h-3.5zM2 5h12v9a1 1 0 0 1-1 1H3a1 1 0 0 1-1-1V5z" />
                          </svg></div>
                      </div>
                    </div>
                  </a>
                </div>

            ))}
        </section>
            </div>
  )
}
