import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


export const ProductNavBarFilter = (props) => {
    const searchByCategory=(category)=>{
    const fetchByCategory = async()=>{
        try {
            const res= await axios.get(`http://localhost:8800/productslist/${category}`)
                    props.setProducts(res.data)
                    console.log(res)
                } catch(err){
                console.log(err);
                }
            };
            fetchByCategory();
        };
    return (
        <div className="relative flex flex-wrap items-center justify-between px-2 py-3 bg-slate-500 mb-3">
        <div className="container px-4 mx-auto flex flex-wrap items-center justify-between">
          <div className="w-full relative flex justify-between lg:w-auto  px-4 lg:static lg:block lg:justify-start">
            <Link className="text-sm font-bold leading-relaxed inline-block mr-4 py-2 whitespace-nowrap uppercase text-white" to="" >
             All Items
            </Link>
            <button className="cursor-pointer text-xl leading-none px-3 py-1 border border-solid border-transparent rounded bg-transparent block lg:hidden outline-none focus:outline-none" type="button">
              <span className="block relative w-6 h-px rounded-sm bg-white"></span>
              <span className="block relative w-6 h-px rounded-sm bg-white mt-1"></span>
              <span className="block relative w-6 h-px rounded-sm bg-white mt-1"></span>
            </button>
          </div>
          <div className="lg:flex flex-grow items-center" id="example-navbar-warning">
            <ul className="flex flex-col lg:flex-row list-none mr-auto">
              <li className="nav-item">
                <a className="px-3 py-2 flex items-center text-xs uppercase font-bold leading-snug text-white hover:opacity-75" href="#pablo">
                  <i className="fab fa-facebook-square text-lg leading-lg text-white opacity-75" /> 
                </a>
              </li>
              <li className="nav-item">
                <button onClick={() =>  searchByCategory (`"Tops"`)} className="px-3 py-2 flex items-center text-xs uppercase font-bold leading-snug text-white hover:opacity-75" >
                  <i className=" text-lg leading-lg text-white opacity-75" /> <span className="ml-2">Tops</span>
                </button>
              </li>
              {/* <li className="nav-item">
                <Link className="px-3 py-2 flex items-center text-xs uppercase font-bold leading-snug text-white hover:opacity-75" to="ProductsBottomsAsc" element={<ProductsBottomsAsc />}>
                  <i className=" text-lg leading-lg text-white opacity-75" /> <span className="ml-2">Bottoms</span>
                </Link>
              </li>
             <li className="nav-item">
                <Link className="px-3 py-2 flex items-center text-xs uppercase font-bold leading-snug text-white hover:opacity-75" to="ProductsDressesAsc" element={<ProductsDressesAsc />}>
                  <i className=" text-lg leading-lg text-white opacity-75" /> <span className="ml-2">Dresses</span>
                </Link>
              </li> */}
            </ul>
          </div>
          {/* Dropdown menu */}
          {/* <DropDownPriceAll/> */}
        </div>
      </div>
  )
}
